(()=>{var n=document.querySelectorAll("[data-toggle-target]");for(let t of n){let e=document.querySelector(t.dataset.toggleTarget),o=t.dataset.toggleClass;t.addEventListener("click",()=>{for(let a of o.split(" "))e.classList.toggle(a)})}(function(){let t="aiway_lang_override";if(localStorage.getItem(t)||window.location.pathname.startsWith("/pt"))return;(navigator.language||navigator.userLanguage||"").toLowerCase().startsWith("pt")&&(localStorage.setItem(t,"auto-pt"),window.location.replace("/pt"+window.location.pathname))})();document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll('a[href^="/pt"], a[href="/"]').forEach(function(t){t.addEventListener("click",function(){this.getAttribute("href").startsWith("/pt")?localStorage.setItem(STORAGE_KEY,"pt"):this.closest("li")&&this.textContent.trim().match(/^(EN|PT)$/)&&localStorage.setItem(STORAGE_KEY,"en")})})});})();
